        for i in 0..outgoing_body_ciphertext_from_typescript.len() {
            assert_eq(ciphertext[i], outgoing_body_ciphertext_from_typescript[i]);
        }
        assert_eq(outgoing_body_ciphertext_from_typescript.len(), ciphertext.len());
    }
}

